import React, { useState } from "react";
import "./AddUser.css";
import Card from "../UI/Card";
import Button from "../UI/Button";
import classes from "../UI/AddUser.module.css";
import ErrorModal from "../UI/ErrorModal";

const AddUser = (props) => {
  const [enteredUsername, setEnteredUsername] = useState('');
  const [enteredAge, setEnteredAge] = useState('');
  const [error, setError] = useState('');

  const usernameChangeHandler = (event) => {
    setEnteredUsername(event.target.value);
  };
  const ageHandler = (event) => {
    setEnteredAge(event.target.value);
  };

  const addUserHandler = (event) => {
    event.preventDefault();
    if (enteredUsername.trim() === 0 || enteredAge.trim() === 0) {
        setError({
            title: 'Ivalid input',
            message: 'Please enter a vallide name and age (non empty values).'
        });
      return;
    }
    if (+enteredAge < 1) {
        setError({
            title: 'Invalid age',
            message: 'please enter a valid age (>0)'
        });
      return;
    }
    props.onAddUser(enteredUsername, enteredAge);
    setEnteredAge("");
    setEnteredUsername("");
  };

  const errorHandler = () => {
    setError(null);
  }

  return (
    <div>
      {error && 
      <ErrorModal title={error.title} message={error.message} />
      }
      <Card className={classes.input}>
        <form onSubmit={addUserHandler}>
          <label htmlFor="Username">Username</label>
          <input
            id="username"
            type="text"
            onChange={usernameChangeHandler}
            value={enteredUsername}
          />
          <label htmlFor="age">Age (Years)</label>
          <input
            id="age"
            type="number"
            onChange={ageHandler}
            value={enteredAge}
          />
          <Button type="submit" onClick={props.onConfirm}>Add User</Button>
        </form>
      </Card>
    </div>
  );
};

export default AddUser;
