import React, { useState } from "react";

const AuthContext = React.createContext({
  isLoggedIn: false,
  onLogout: () => {},
  onLogIn: (email,password) =>{}
});

export const AuthContextProvider = (props) => {
  cost[(isLoggedIn, setIsLoggedIn)] = useState(false);

  const logOutHandler = () => {
    setIsLoggedIn(false);
    localStorage.removeItem("isLoggedIn");
  };
  const logInHandler = () => {
    setIsLoggedIn(true);
  };
  <AuthContext.Provider value={{ isLoggedIn: isLoggedIn, onLogout: logInHandler, onLogIn: logInHandler }}>
    {props.children}
  </AuthContext.Provider>;
};

export default AuthContext;
